namespace Shine
{
	/** 创建角色数据(generated by shine) */
	export class CreatePlayerData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.CreatePlayer;
		
		/** 名字 */
		public name:string;
		
		/** 性别 */
		public sex:number;
		
		/** 职业 */
		public vocation:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.CreatePlayer;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CreatePlayerData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.name=stream.readUTF();
			
			this.sex=stream.readInt();
			
			this.vocation=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeUTF(this.name);
			
			stream.writeInt(this.sex);
			
			stream.writeInt(this.vocation);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof CreatePlayerData))
				return;
			
			var mData:CreatePlayerData=data as CreatePlayerData;
			
			this.name=mData.name;
			this.sex=mData.sex;
			this.vocation=mData.vocation;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof CreatePlayerData))
				return;
			
			var mData:CreatePlayerData=data as CreatePlayerData;
			
			this.name=mData.name;
			
			this.sex=mData.sex;
			
			this.vocation=mData.vocation;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
