namespace Shine
{
	/** 角色切换游戏服额外传输数据(在PlayerList之外)(generated by shine) */
	export class PlayerSwitchGameData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.PlayerSwitchGame;
		
		/** 是否需要中心服角色显示信息 */
		public needPlayerRoleShowForCenter:boolean;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.PlayerSwitchGame;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "PlayerSwitchGameData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.needPlayerRoleShowForCenter=stream.readBoolean();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeBoolean(this.needPlayerRoleShowForCenter);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof PlayerSwitchGameData))
				return;
			
			var mData:PlayerSwitchGameData=data as PlayerSwitchGameData;
			
			this.needPlayerRoleShowForCenter=mData.needPlayerRoleShowForCenter;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof PlayerSwitchGameData))
				return;
			
			var mData:PlayerSwitchGameData=data as PlayerSwitchGameData;
			
			this.needPlayerRoleShowForCenter=mData.needPlayerRoleShowForCenter;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
