namespace Shine
{
	/** 成就完成数据(generated by shine) */
	export class AchievementCompleteData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.AchievementComplete;
		
		/** id */
		public id:number;
		
		/** 完成时间戳 */
		public time:number;
		
		/** 是否领过奖励 */
		public hasGotReward:boolean;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.AchievementComplete;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AchievementCompleteData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			this.time=stream.readLong();
			
			this.hasGotReward=stream.readBoolean();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			stream.writeLong(this.time);
			
			stream.writeBoolean(this.hasGotReward);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof AchievementCompleteData))
				return;
			
			var mData:AchievementCompleteData=data as AchievementCompleteData;
			
			this.id=mData.id;
			this.time=mData.time;
			this.hasGotReward=mData.hasGotReward;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof AchievementCompleteData))
				return;
			
			var mData:AchievementCompleteData=data as AchievementCompleteData;
			
			this.id=mData.id;
			
			this.time=mData.time;
			
			this.hasGotReward=mData.hasGotReward;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
