namespace Shine
{
	/** 成就数据(generated by shine) */
	export class AchievementData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.Achievement;
		
		/** 成就ID */
		public id:number;
		
		/** 任务目标数据 */
		public task:TaskData;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.Achievement;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AchievementData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			this.task=stream.readDataSimpleNotNull() as TaskData;
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			stream.writeDataSimpleNotNull(this.task);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof AchievementData))
				return;
			
			var mData:AchievementData=data as AchievementData;
			
			this.id=mData.id;
			this.task=mData.task;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof AchievementData))
				return;
			
			var mData:AchievementData=data as AchievementData;
			
			this.id=mData.id;
			
			this.task=mData.task.clone() as TaskData;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			this.task=new TaskData();
			this.task.initDefault();
		}
		
	}
}
