namespace Shine
{
	/** 成就存库数据(generated by shine) */
	export class AchievementSaveData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.AchievementSave;
		
		/** 成就ID */
		public id:number;
		
		/** 完成进度 */
		public num:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.AchievementSave;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "AchievementSaveData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			this.num=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			stream.writeInt(this.num);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof AchievementSaveData))
				return;
			
			var mData:AchievementSaveData=data as AchievementSaveData;
			
			this.id=mData.id;
			this.num=mData.num;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof AchievementSaveData))
				return;
			
			var mData:AchievementSaveData=data as AchievementSaveData;
			
			this.id=mData.id;
			
			this.num=mData.num;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
