namespace Shine
{
	/** 任务完成数据(周期类用)(generated by shine) */
	export class QuestCompleteData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.QuestComplete;
		
		/** 任务ID */
		public id:number;
		
		/** 恢复时间戳 */
		public reTime:number;
		
		/** 是否成功完成(否则是失败) */
		public isSuccess:boolean;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.QuestComplete;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "QuestCompleteData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			this.reTime=stream.readLong();
			
			this.isSuccess=stream.readBoolean();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			stream.writeLong(this.reTime);
			
			stream.writeBoolean(this.isSuccess);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof QuestCompleteData))
				return;
			
			var mData:QuestCompleteData=data as QuestCompleteData;
			
			this.id=mData.id;
			this.reTime=mData.reTime;
			this.isSuccess=mData.isSuccess;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof QuestCompleteData))
				return;
			
			var mData:QuestCompleteData=data as QuestCompleteData;
			
			this.id=mData.id;
			
			this.reTime=mData.reTime;
			
			this.isSuccess=mData.isSuccess;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
