namespace Shine
{
	/** 主单位存库数据(generated by shine) */
	export class MUnitSaveData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.MUnitSave;
		
		/** 角色ID */
		public id:number;
		
		/** 缓存数据 */
		public cache:MUnitCacheData;
		
		/** 装备组 */
		public equips:EquipContainerData;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.MUnitSave;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "MUnitSaveData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			if(stream.readBoolean())
			{
				this.cache=stream.readDataSimpleNotNull() as MUnitCacheData;
			}
			else
			{
				this.cache=null;
			}
			
			this.equips=stream.readDataSimpleNotNull() as EquipContainerData;
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			if(this.cache!=null)
			{
				stream.writeBoolean(true);
				stream.writeDataSimpleNotNull(this.cache);
			}
			else
			{
				stream.writeBoolean(false);
			}
			
			stream.writeDataSimpleNotNull(this.equips);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof MUnitSaveData))
				return;
			
			var mData:MUnitSaveData=data as MUnitSaveData;
			
			this.id=mData.id;
			this.cache=mData.cache;
			this.equips=mData.equips;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof MUnitSaveData))
				return;
			
			var mData:MUnitSaveData=data as MUnitSaveData;
			
			this.id=mData.id;
			
			if(mData.cache!=null)
			{
				this.cache=mData.cache.clone() as MUnitCacheData;
			}
			else
			{
				this.cache=null;
			}
			
			this.equips=mData.equips.clone() as EquipContainerData;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			this.equips=new EquipContainerData();
			this.equips.initDefault();
		}
		
	}
}
