namespace Shine
{
	/** 宠物存库数据(generated by shine) */
	export class PetSaveData extends MUnitSaveData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.PetSave;
		
		/** 宠物等级 */
		public level:number;
		
		/** 宠物经验 */
		public exp:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.PetSave;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "PetSaveData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.level=stream.readInt();
			
			this.exp=stream.readLong();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.level);
			
			stream.writeLong(this.exp);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof PetSaveData))
				return;
			
			var mData:PetSaveData=data as PetSaveData;
			
			this.level=mData.level;
			this.exp=mData.exp;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof PetSaveData))
				return;
			
			var mData:PetSaveData=data as PetSaveData;
			
			this.level=mData.level;
			
			this.exp=mData.exp;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
		}
		
	}
}
