namespace Shine
{
	/** 冷却数据(generated by shine) */
	export class CDData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.CD;
		
		/** id */
		public id:number;
		
		/** 经过时间 */
		public timePass:number;
		
		/** 时间上限 */
		public timeMax:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.CD;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "CDData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			this.timePass=stream.readInt();
			
			this.timeMax=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			stream.writeInt(this.timePass);
			
			stream.writeInt(this.timeMax);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof CDData))
				return;
			
			var mData:CDData=data as CDData;
			
			this.id=mData.id;
			this.timePass=mData.timePass;
			this.timeMax=mData.timeMax;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof CDData))
				return;
			
			var mData:CDData=data as CDData;
			
			this.id=mData.id;
			
			this.timePass=mData.timePass;
			
			this.timeMax=mData.timeMax;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
