namespace Shine
{
	/** 朝向数据(generated by shine) */
	export class DirData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.Dir;
		
		/** 平面朝向传输(弧度)(xz平面)(y轴)(0为x轴正向,计算用) */
		public idirection:number;
		
		/** 俯仰轴朝向传输(弧度)(yz平面)(x轴) */
		public idirectionX:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.Dir;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "DirData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.idirection=stream.readInt();
			
			this.idirectionX=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.idirection);
			
			stream.writeInt(this.idirectionX);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof DirData))
				return;
			
			var mData:DirData=data as DirData;
			
			this.idirection=mData.idirection;
			this.idirectionX=mData.idirectionX;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof DirData))
				return;
			
			var mData:DirData=data as DirData;
			
			this.idirection=mData.idirection;
			
			this.idirectionX=mData.idirectionX;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
