namespace Shine
{
	/** 位置数据(generated by shine) */
	export class PosData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.Pos;
		
		/** x传输 */
		public ix:number;
		
		/** y传输 */
		public iy:number;
		
		/** z传输 */
		public iz:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.Pos;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "PosData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.ix=stream.readInt();
			
			this.iy=stream.readInt();
			
			this.iz=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.ix);
			
			stream.writeInt(this.iy);
			
			stream.writeInt(this.iz);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof PosData))
				return;
			
			var mData:PosData=data as PosData;
			
			this.ix=mData.ix;
			this.iy=mData.iy;
			this.iz=mData.iz;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof PosData))
				return;
			
			var mData:PosData=data as PosData;
			
			this.ix=mData.ix;
			
			this.iy=mData.iy;
			
			this.iz=mData.iz;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
