namespace Shine
{
	/** 矩形数据(generated by shine) */
	export class RectData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.Rect;
		
		/** x传输 */
		public ix:number;
		
		/** y传输 */
		public iy:number;
		
		/** width传输 */
		public iWidth:number;
		
		/** height传输 */
		public iHeight:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.Rect;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.ix=stream.readInt();
			
			this.iy=stream.readInt();
			
			this.iWidth=stream.readInt();
			
			this.iHeight=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.ix);
			
			stream.writeInt(this.iy);
			
			stream.writeInt(this.iWidth);
			
			stream.writeInt(this.iHeight);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof RectData))
				return;
			
			var mData:RectData=data as RectData;
			
			this.ix=mData.ix;
			this.iy=mData.iy;
			this.iWidth=mData.iWidth;
			this.iHeight=mData.iHeight;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof RectData))
				return;
			
			var mData:RectData=data as RectData;
			
			this.ix=mData.ix;
			
			this.iy=mData.iy;
			
			this.iWidth=mData.iWidth;
			
			this.iHeight=mData.iHeight;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
