namespace Shine
{
	/** 技能数据(generated by shine) */
	export class SkillData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.Skill;
		
		/** 技能ID */
		public id:number;
		
		/** 技能等级 */
		public level:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.Skill;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SkillData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			this.level=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			stream.writeInt(this.level);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof SkillData))
				return;
			
			var mData:SkillData=data as SkillData;
			
			this.id=mData.id;
			this.level=mData.level;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof SkillData))
				return;
			
			var mData:SkillData=data as SkillData;
			
			this.id=mData.id;
			
			this.level=mData.level;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
