namespace Shine
{
	/** 角色势力数据(generated by shine) */
	export class RoleForceData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.RoleForce;
		
		/** 势力 */
		public force:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.RoleForce;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RoleForceData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.force=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.force);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof RoleForceData))
				return;
			
			var mData:RoleForceData=data as RoleForceData;
			
			this.force=mData.force;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof RoleForceData))
				return;
			
			var mData:RoleForceData=data as RoleForceData;
			
			this.force=mData.force;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
