namespace Shine
{
	/** 场景角色数据(generated by shine) */
	export class SceneRoleData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.SceneRole;
		
		/** 角色ID */
		public playerID:number;
		
		/** 角色显示数据(中立角色为空) */
		public showData:RoleShowData;
		
		/** 属性数据 */
		public attribute:RoleAttributeData;
		
		/** 势力数据 */
		public force:RoleForceData;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.SceneRole;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SceneRoleData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.playerID=stream.readLong();
			
			if(stream.readBoolean())
			{
				this.showData=stream.readDataSimpleNotNull() as RoleShowData;
			}
			else
			{
				this.showData=null;
			}
			
			if(stream.readBoolean())
			{
				this.attribute=stream.readDataSimpleNotNull() as RoleAttributeData;
			}
			else
			{
				this.attribute=null;
			}
			
			this.force=stream.readDataSimpleNotNull() as RoleForceData;
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeLong(this.playerID);
			
			if(this.showData!=null)
			{
				stream.writeBoolean(true);
				stream.writeDataSimpleNotNull(this.showData);
			}
			else
			{
				stream.writeBoolean(false);
			}
			
			if(this.attribute!=null)
			{
				stream.writeBoolean(true);
				stream.writeDataSimpleNotNull(this.attribute);
			}
			else
			{
				stream.writeBoolean(false);
			}
			
			stream.writeDataSimpleNotNull(this.force);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof SceneRoleData))
				return;
			
			var mData:SceneRoleData=data as SceneRoleData;
			
			this.playerID=mData.playerID;
			this.showData=mData.showData;
			this.attribute=mData.attribute;
			this.force=mData.force;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof SceneRoleData))
				return;
			
			var mData:SceneRoleData=data as SceneRoleData;
			
			this.playerID=mData.playerID;
			
			if(mData.showData!=null)
			{
				this.showData=mData.showData.clone() as RoleShowData;
			}
			else
			{
				this.showData=null;
			}
			
			if(mData.attribute!=null)
			{
				this.attribute=mData.attribute.clone() as RoleAttributeData;
			}
			else
			{
				this.attribute=null;
			}
			
			this.force=mData.force.clone() as RoleForceData;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			this.force=new RoleForceData();
			this.force.initDefault();
		}
		
	}
}
