namespace Shine
{
	/** 副本类场景进入数据(generated by shine) */
	export class BattleSceneEnterData extends SceneEnterData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.BattleSceneEnter;
		
		/** 副本当前状态 */
		public state:number;
		
		/** 剩余tick时间(ms)(-1就是没在tick) */
		public timeTick:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.BattleSceneEnter;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "BattleSceneEnterData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.state=stream.readInt();
			
			this.timeTick=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.state);
			
			stream.writeInt(this.timeTick);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof BattleSceneEnterData))
				return;
			
			var mData:BattleSceneEnterData=data as BattleSceneEnterData;
			
			this.state=mData.state;
			this.timeTick=mData.timeTick;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof BattleSceneEnterData))
				return;
			
			var mData:BattleSceneEnterData=data as BattleSceneEnterData;
			
			this.state=mData.state;
			
			this.timeTick=mData.timeTick;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
		}
		
	}
}
