namespace Shine
{
	/** 创建场景数据(generated by shine) */
	export class CreateSceneData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.CreateScene;
		
		public sceneID:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.CreateScene;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.sceneID=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.sceneID);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof CreateSceneData))
				return;
			
			var mData:CreateSceneData=data as CreateSceneData;
			
			this.sceneID=mData.sceneID;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof CreateSceneData))
				return;
			
			var mData:CreateSceneData=data as CreateSceneData;
			
			this.sceneID=mData.sceneID;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
