namespace Shine
{
	/** 单位通用数据(generated by shine) */
	export class UnitNormalData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.UnitNormal;
		
		/** 归属角色 */
		public belongPlayerID:number=-1;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.UnitNormal;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.belongPlayerID=stream.readLong();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeLong(this.belongPlayerID);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof UnitNormalData))
				return;
			
			var mData:UnitNormalData=data as UnitNormalData;
			
			this.belongPlayerID=mData.belongPlayerID;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof UnitNormalData))
				return;
			
			var mData:UnitNormalData=data as UnitNormalData;
			
			this.belongPlayerID=mData.belongPlayerID;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
