namespace Shine
{
	/** 建筑身份数据(generated by shine) */
	export class BuildingIdentityData extends FightUnitIdentityData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.BuildingIdentity;
		
		/** 建筑状态(见BuildingStateType) */
		public state:number;
		
		/** 建造/升级 经过时间 */
		public passTime:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.BuildingIdentity;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "BuildingIdentityData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.state=stream.readInt();
			
			this.passTime=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.state);
			
			stream.writeInt(this.passTime);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof BuildingIdentityData))
				return;
			
			var mData:BuildingIdentityData=data as BuildingIdentityData;
			
			this.state=mData.state;
			this.passTime=mData.passTime;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof BuildingIdentityData))
				return;
			
			var mData:BuildingIdentityData=data as BuildingIdentityData;
			
			this.state=mData.state;
			
			this.passTime=mData.passTime;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
		}
		
	}
}
