namespace Shine
{
	/** 掉落物品身份数据(generated by shine) */
	export class FieldItemIdentityData extends UnitIdentityData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.FieldItemIdentity;
		
		/** 物品数据 */
		public item:ItemData;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.FieldItemIdentity;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FieldItemIdentityData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.item=stream.readDataSimpleNotNull() as ItemData;
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeDataSimpleNotNull(this.item);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof FieldItemIdentityData))
				return;
			
			var mData:FieldItemIdentityData=data as FieldItemIdentityData;
			
			this.item=mData.item;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof FieldItemIdentityData))
				return;
			
			var mData:FieldItemIdentityData=data as FieldItemIdentityData;
			
			this.item=mData.item.clone() as ItemData;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
			this.item=new ItemData();
			this.item.initDefault();
		}
		
	}
}
