namespace Shine
{
	/** 傀儡单位身份数据(generated by shine) */
	export class PuppetIdentityData extends FightUnitIdentityData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.PuppetIdentity;
		
		/** 控制者实例ID */
		public masterInstanceID:number;
		
		/** 剩余时间 */
		public lastTime:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.PuppetIdentity;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "PuppetIdentityData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.masterInstanceID=stream.readInt();
			
			this.lastTime=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.masterInstanceID);
			
			stream.writeInt(this.lastTime);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof PuppetIdentityData))
				return;
			
			var mData:PuppetIdentityData=data as PuppetIdentityData;
			
			this.masterInstanceID=mData.masterInstanceID;
			this.lastTime=mData.lastTime;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof PuppetIdentityData))
				return;
			
			var mData:PuppetIdentityData=data as PuppetIdentityData;
			
			this.masterInstanceID=mData.masterInstanceID;
			
			this.lastTime=mData.lastTime;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
		}
		
	}
}
