namespace Shine
{
	/** 场景特效身份数据(generated by shine) */
	export class SceneEffectIdentityData extends UnitIdentityData
	{
		/** id */
		public id:number;
		
		/** 数据类型ID */
		public static dataID:number=BaseDataType.SceneEffectIdentity;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.SceneEffectIdentity;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SceneEffectIdentityData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.id=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.id);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof SceneEffectIdentityData))
				return;
			
			var mData:SceneEffectIdentityData=data as SceneEffectIdentityData;
			
			this.id=mData.id;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof SceneEffectIdentityData))
				return;
			
			var mData:SceneEffectIdentityData=data as SceneEffectIdentityData;
			
			this.id=mData.id;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
		}
		
	}
}
