namespace Shine
{
	/** 角色社交数据(generated by shine) */
	export class RoleSocialData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.RoleSocial;
		
		/** 显示数据 */
		public showData:RoleShowData;
		
		/** 是否在线 */
		public isOnline:boolean;
		
		/** 所在场景位置数据 */
		public location:SceneLocationData;
		
		/** 上次在线时间(如已在线则为-1) */
		public lastOnlineTime:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.RoleSocial;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "RoleSocialData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.showData=stream.readDataSimpleNotNull() as RoleShowData;
			
			this.isOnline=stream.readBoolean();
			
			this.lastOnlineTime=stream.readLong();
			
			if(stream.readBoolean())
			{
				this.location=stream.readDataSimpleNotNull() as SceneLocationData;
			}
			else
			{
				this.location=null;
			}
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeDataSimpleNotNull(this.showData);
			
			stream.writeBoolean(this.isOnline);
			
			stream.writeLong(this.lastOnlineTime);
			
			if(this.location!=null)
			{
				stream.writeBoolean(true);
				stream.writeDataSimpleNotNull(this.location);
			}
			else
			{
				stream.writeBoolean(false);
			}
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof RoleSocialData))
				return;
			
			var mData:RoleSocialData=data as RoleSocialData;
			
			this.showData=mData.showData;
			this.isOnline=mData.isOnline;
			this.lastOnlineTime=mData.lastOnlineTime;
			this.location=mData.location;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof RoleSocialData))
				return;
			
			var mData:RoleSocialData=data as RoleSocialData;
			
			this.showData=mData.showData.clone() as RoleShowData;
			
			this.isOnline=mData.isOnline;
			
			this.lastOnlineTime=mData.lastOnlineTime;
			
			if(mData.location!=null)
			{
				this.location=mData.location.clone() as SceneLocationData;
			}
			else
			{
				this.location=null;
			}
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			this.showData=new RoleShowData();
			this.showData.initDefault();
		}
		
	}
}
