namespace Shine
{
	/** 聊天数据(generated by shine) */
	export class ChatData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.Chat;
		
		/** 类型 */
		public type:number;
		
		/** 文字 */
		public text:string;
		
		/** 聊天元素组 */
		public elements:SList<ChatElementData>;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.Chat;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ChatData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.type=stream.readInt();
			
			this.text=stream.readUTF();
			
			if(stream.readBoolean())
			{
				var elementsLen:number=stream.readLen();
				if(this.elements!=null)
				{
					this.elements.clear();
					this.elements.ensureCapacity(elementsLen);
				}
				else
				{
					this.elements=new SList<ChatElementData>();
				}
				
				var elementsT:SList<ChatElementData>=this.elements;
				for(var elementsI:number=elementsLen-1;elementsI>=0;--elementsI)
				{
					var elementsV:ChatElementData;
					if(stream.readBoolean())
					{
						elementsV=stream.readDataSimpleNotNull() as ChatElementData;
					}
					else
					{
						elementsV=null;
					}
					
					elementsT.add(elementsV);
				}
			}
			else
			{
				this.elements=null;
			}
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.type);
			
			stream.writeUTF(this.text);
			
			if(this.elements!=null)
			{
				stream.writeBoolean(true);
				stream.writeLen(this.elements.size());
				if(!this.elements.isEmpty())
				{
					for(var elementsVI=0,elementsVLen=this.elements.length;elementsVI<elementsVLen;++elementsVI)
					{
						var elementsV:ChatElementData=this.elements[elementsVI];
						if(elementsV!=null)
						{
							stream.writeBoolean(true);
							stream.writeDataSimpleNotNull(elementsV);
						}
						else
						{
							stream.writeBoolean(false);
						}
						
					}
				}
			}
			else
			{
				stream.writeBoolean(false);
			}
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof ChatData))
				return;
			
			var mData:ChatData=data as ChatData;
			
			this.type=mData.type;
			this.text=mData.text;
			this.elements=mData.elements;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof ChatData))
				return;
			
			var mData:ChatData=data as ChatData;
			
			this.type=mData.type;
			
			this.text=mData.text;
			
			if(mData.elements!=null)
			{
				if(this.elements!=null)
				{
					this.elements.clear();
					this.elements.ensureCapacity(mData.elements.size());
				}
				else
				{
					this.elements=new SList<ChatElementData>();
				}
				
				var elementsT:SList<ChatElementData>=this.elements;
				if(!mData.elements.isEmpty())
				{
					for(var elementsVI=0,elementsVLen=mData.elements.length;elementsVI<elementsVLen;++elementsVI)
					{
						var elementsV:ChatElementData=mData.elements[elementsVI];
						var elementsU:ChatElementData;
						if(elementsV!=null)
						{
							elementsU=elementsV.clone() as ChatElementData;
						}
						else
						{
							elementsU=null;
						}
						
						elementsT.add(elementsU);
					}
				}
			}
			else
			{
				this.elements=null;
			}
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
