namespace Shine
{
	/** 聊天元素数据(generated by shine) */
	export class ChatElementData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.ChatElement;
		
		/** 元素类型 */
		public type:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.ChatElement;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ChatElementData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.type=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.type);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof ChatElementData))
				return;
			
			var mData:ChatElementData=data as ChatElementData;
			
			this.type=mData.type;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof ChatElementData))
				return;
			
			var mData:ChatElementData=data as ChatElementData;
			
			this.type=mData.type;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
