namespace Shine
{
	/** 物品聊天元素数据(generated by shine) */
	export class ItemChatElementData extends ChatElementData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.ItemChatElement;
		
		/** 物品数据 */
		public data:ItemData;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.ItemChatElement;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.data=stream.readDataSimpleNotNull() as ItemData;
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeDataSimpleNotNull(this.data);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof ItemChatElementData))
				return;
			
			var mData:ItemChatElementData=data as ItemChatElementData;
			
			this.data=mData.data;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof ItemChatElementData))
				return;
			
			var mData:ItemChatElementData=data as ItemChatElementData;
			
			this.data=mData.data.clone() as ItemData;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
			this.data=new ItemData();
			this.data.initDefault();
		}
		
	}
}
