namespace Shine
{
	/** 角色聊天数据(generated by shine) */
	export class RoleChatData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.RoleChat;
		
		/** 显示数据 */
		public showData:RoleSimpleShowData;
		
		/** 聊天数据 */
		public chatData:ChatData;
		
		/** 时间 */
		public time:number;
		
		/** 发送序号 */
		public sendIndex:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.RoleChat;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.showData=stream.readDataSimpleNotNull() as RoleSimpleShowData;
			
			this.chatData=stream.readDataSimpleNotNull() as ChatData;
			
			this.time=stream.readLong();
			
			this.sendIndex=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeDataSimpleNotNull(this.showData);
			
			stream.writeDataSimpleNotNull(this.chatData);
			
			stream.writeLong(this.time);
			
			stream.writeInt(this.sendIndex);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof RoleChatData))
				return;
			
			var mData:RoleChatData=data as RoleChatData;
			
			this.showData=mData.showData;
			this.chatData=mData.chatData;
			this.time=mData.time;
			this.sendIndex=mData.sendIndex;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof RoleChatData))
				return;
			
			var mData:RoleChatData=data as RoleChatData;
			
			this.showData=mData.showData.clone() as RoleSimpleShowData;
			
			this.chatData=mData.chatData.clone() as ChatData;
			
			this.time=mData.time;
			
			this.sendIndex=mData.sendIndex;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			this.showData=new RoleSimpleShowData();
			this.showData.initDefault();
			this.chatData=new ChatData();
			this.chatData.initDefault();
		}
		
	}
}
