namespace Shine
{
	/** 推送玩家聊天(generated by shine) */
	export class SendPlayerChatOWData extends PlayerWorkData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.SendPlayerChatOW;
		
		/** 聊天数据 */
		public chatData:RoleChatData;
		
		/** 频道 */
		public channel:number;
		
		/** 二级索引 */
		public key:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.SendPlayerChatOW;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.chatData=stream.readDataSimpleNotNull() as RoleChatData;
			
			this.channel=stream.readInt();
			
			this.key=stream.readLong();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeDataSimpleNotNull(this.chatData);
			
			stream.writeInt(this.channel);
			
			stream.writeLong(this.key);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof SendPlayerChatOWData))
				return;
			
			var mData:SendPlayerChatOWData=data as SendPlayerChatOWData;
			
			this.chatData=mData.chatData;
			this.channel=mData.channel;
			this.key=mData.key;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof SendPlayerChatOWData))
				return;
			
			var mData:SendPlayerChatOWData=data as SendPlayerChatOWData;
			
			this.chatData=mData.chatData.clone() as RoleChatData;
			
			this.channel=mData.channel;
			
			this.key=mData.key;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
			this.chatData=new RoleChatData();
			this.chatData.initDefault();
		}
		
	}
}
