namespace Shine
{
	/** 申请添加好友数据(generated by shine) */
	export class ApplyAddFriendData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.ApplyAddFriend;
		
		/** 角色ID */
		public playerID:number;
		
		/** 社交数据 */
		public data:RoleSocialData;
		
		/** 申请时间 */
		public applyTime:number;
		
		/** 添加类型 */
		public type:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.ApplyAddFriend;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ApplyAddFriendData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.playerID=stream.readLong();
			
			this.data=stream.readDataSimpleNotNull() as RoleSocialData;
			
			this.applyTime=stream.readLong();
			
			this.type=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeLong(this.playerID);
			
			stream.writeDataSimpleNotNull(this.data);
			
			stream.writeLong(this.applyTime);
			
			stream.writeInt(this.type);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof ApplyAddFriendData))
				return;
			
			var mData:ApplyAddFriendData=data as ApplyAddFriendData;
			
			this.playerID=mData.playerID;
			this.data=mData.data;
			this.applyTime=mData.applyTime;
			this.type=mData.type;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof ApplyAddFriendData))
				return;
			
			var mData:ApplyAddFriendData=data as ApplyAddFriendData;
			
			this.playerID=mData.playerID;
			
			this.data=mData.data.clone() as RoleSocialData;
			
			this.applyTime=mData.applyTime;
			
			this.type=mData.type;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			this.data=new RoleSocialData();
			this.data.initDefault();
		}
		
	}
}
