namespace Shine
{
	/** 好友数据(maybeExtends)(generated by shine) */
	export class FriendData extends ContactData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.Friend;
		
		/** 添加时间 */
		public addTime:number;
		
		/** 是否是机器人好友 */
		public isRobot:boolean;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.Friend;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FriendData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.addTime=stream.readLong();
			
			this.isRobot=stream.readBoolean();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.addTime);
			
			stream.writeBoolean(this.isRobot);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof FriendData))
				return;
			
			var mData:FriendData=data as FriendData;
			
			this.addTime=mData.addTime;
			this.isRobot=mData.isRobot;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof FriendData))
				return;
			
			var mData:FriendData=data as FriendData;
			
			this.addTime=mData.addTime;
			
			this.isRobot=mData.isRobot;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
		}
		
	}
}
