namespace Shine
{
	/** 玩家群简版数据(generated by shine) */
	export class RoleGroupSimpleData extends KeyData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.RoleGroupSimple;
		
		/** 群id */
		public groupID:number;
		
		/** 等级 */
		public level:number;
		
		/** 群名 */
		public name:string;
		
		/** 成员数 */
		public memberNum:number;
		
		/** 公告 */
		public notice:string;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.RoleGroupSimple;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.groupID=stream.readLong();
			
			this.level=stream.readInt();
			
			this.name=stream.readUTF();
			
			this.notice=stream.readUTF();
			
			this.memberNum=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.groupID);
			
			stream.writeInt(this.level);
			
			stream.writeUTF(this.name);
			
			stream.writeUTF(this.notice);
			
			stream.writeInt(this.memberNum);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof RoleGroupSimpleData))
				return;
			
			var mData:RoleGroupSimpleData=data as RoleGroupSimpleData;
			
			this.groupID=mData.groupID;
			this.level=mData.level;
			this.name=mData.name;
			this.notice=mData.notice;
			this.memberNum=mData.memberNum;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof RoleGroupSimpleData))
				return;
			
			var mData:RoleGroupSimpleData=data as RoleGroupSimpleData;
			
			this.groupID=mData.groupID;
			
			this.level=mData.level;
			
			this.name=mData.name;
			
			this.notice=mData.notice;
			
			this.memberNum=mData.memberNum;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
		}
		
	}
}
