namespace Shine
{
	/** 区服全局数据(generated by shine) */
	export class AreaGlobalWorkData extends WorkData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.AreaGlobalWork;
		
		public receiveAreaID:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.AreaGlobalWork;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.receiveAreaID=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.receiveAreaID);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof AreaGlobalWorkData))
				return;
			
			var mData:AreaGlobalWorkData=data as AreaGlobalWorkData;
			
			this.receiveAreaID=mData.receiveAreaID;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof AreaGlobalWorkData))
				return;
			
			var mData:AreaGlobalWorkData=data as AreaGlobalWorkData;
			
			this.receiveAreaID=mData.receiveAreaID;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
		}
		
	}
}
