namespace Shine
{
	/** 区服服务器数据(generated by shine) */
	export class AreaServerData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.AreaServer;
		
		/** 区服ID */
		public areaID:number;
		
		/** 负载值 */
		public load:number;
		
		/** 是否限制注册 */
		public isLimitRegist:boolean;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.AreaServer;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.areaID=stream.readInt();
			
			this.load=stream.readInt();
			
			this.isLimitRegist=stream.readBoolean();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.areaID);
			
			stream.writeInt(this.load);
			
			stream.writeBoolean(this.isLimitRegist);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof AreaServerData))
				return;
			
			var mData:AreaServerData=data as AreaServerData;
			
			this.areaID=mData.areaID;
			this.load=mData.load;
			this.isLimitRegist=mData.isLimitRegist;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof AreaServerData))
				return;
			
			var mData:AreaServerData=data as AreaServerData;
			
			this.areaID=mData.areaID;
			
			this.load=mData.load;
			
			this.isLimitRegist=mData.isLimitRegist;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
