namespace Shine
{
	/** 游戏服运行数据(generated by shine) */
	export class GameServerRunData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.GameServerRun;
		
		/** 在线人数 */
		public onlineNum:number;
		
		/** gameID */
		public id:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.GameServerRun;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "GameServerRunData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			this.onlineNum=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			stream.writeInt(this.onlineNum);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof GameServerRunData))
				return;
			
			var mData:GameServerRunData=data as GameServerRunData;
			
			this.id=mData.id;
			this.onlineNum=mData.onlineNum;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof GameServerRunData))
				return;
			
			var mData:GameServerRunData=data as GameServerRunData;
			
			this.id=mData.id;
			
			this.onlineNum=mData.onlineNum;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
