namespace Shine
{
	/** 中心服简版连接数据(generated by shine) */
	export class ServerSimpleInfoData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.ServerSimpleInfo;
		
		/** ID */
		public id:number;
		
		/** 服务器地址 */
		public serverHost:string;
		
		/** 服务器端口 */
		public serverPort:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.ServerSimpleInfo;
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ServerSimpleInfoData";
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.id=stream.readInt();
			
			this.serverHost=stream.readUTF();
			
			this.serverPort=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeInt(this.id);
			
			stream.writeUTF(this.serverHost);
			
			stream.writeInt(this.serverPort);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof ServerSimpleInfoData))
				return;
			
			var mData:ServerSimpleInfoData=data as ServerSimpleInfoData;
			
			this.id=mData.id;
			this.serverHost=mData.serverHost;
			this.serverPort=mData.serverPort;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof ServerSimpleInfoData))
				return;
			
			var mData:ServerSimpleInfoData=data as ServerSimpleInfoData;
			
			this.id=mData.id;
			
			this.serverHost=mData.serverHost;
			
			this.serverPort=mData.serverPort;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
