namespace Shine
{
	/** 用户事务(login服执行,要求事务本身幂等，因为login上目前没做事务序号存储)(generated by shine) */
	export class UserWorkData extends WorkData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.UserWork;
		
		/** uid */
		public uid:string;
		
		/** userID */
		public userID:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.UserWork;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			super.toReadBytesSimple(stream);
			
			this.uid=stream.readUTF();
			
			this.userID=stream.readLong();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeUTF(this.uid);
			
			stream.writeLong(this.userID);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			super.toShadowCopy(data);
			
			if(!(data instanceof UserWorkData))
				return;
			
			var mData:UserWorkData=data as UserWorkData;
			
			this.uid=mData.uid;
			this.userID=mData.userID;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			super.toCopy(data);
			
			if(!(data instanceof UserWorkData))
				return;
			
			var mData:UserWorkData=data as UserWorkData;
			
			this.uid=mData.uid;
			
			this.userID=mData.userID;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			super.initDefault();
			
		}
		
	}
}
