namespace Shine
{
	/** 事务完成数据(generated by shine) */
	export class WorkCompleteData extends BaseData
	{
		/** 数据类型ID */
		public static dataID:number=BaseDataType.WorkComplete;
		
		/** 实例ID */
		public workInstanceID:number;
		
		/** 发起者索引(type+id) */
		public senderIndex:number;
		
		constructor()
		{
			super();
			this._dataID=BaseDataType.WorkComplete;
		}
		
		/** 读取字节流(简版) */
		protected toReadBytesSimple(stream:BytesReadStream):void
		{
			this.workInstanceID=stream.readLong();
			
			this.senderIndex=stream.readInt();
			
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			stream.writeLong(this.workInstanceID);
			
			stream.writeInt(this.senderIndex);
			
		}
		
		/** 复制(潜拷贝) */
		protected toShadowCopy(data:BaseData):void
		{
			if(!(data instanceof WorkCompleteData))
				return;
			
			var mData:WorkCompleteData=data as WorkCompleteData;
			
			this.workInstanceID=mData.workInstanceID;
			this.senderIndex=mData.senderIndex;
		}
		
		/** 复制(深拷贝) */
		protected toCopy(data:BaseData):void
		{
			if(!(data instanceof WorkCompleteData))
				return;
			
			var mData:WorkCompleteData=data as WorkCompleteData;
			
			this.workInstanceID=mData.workInstanceID;
			
			this.senderIndex=mData.senderIndex;
			
		}
		
		/** 初始化初值 */
		public initDefault():void
		{
			
		}
		
	}
}
