namespace Shine
{
	/** 功能接收消息(generated by shine) */
	export class FuncRRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncR;
		
		/** 功能ID */
		public funcID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncR;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FuncRRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.funcID);
			
		}
		
		/** 创建实例 */
		public static createFuncR(funcID:number):FuncRRequest
		{
			var re:FuncRRequest=new FuncRRequest();
			re.funcID=funcID;
			return re;
		}
		
	}
}
