namespace Shine
{
	/** 移动装备消息(generated by shine) */
	export class FuncMoveEquipRequest extends FuncRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncMoveEquip;
		
		public fromSlot:number;
		
		public toSlot:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncMoveEquip;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FuncMoveEquipRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.fromSlot);
			
			stream.writeInt(this.toSlot);
			
		}
		
		/** 创建实例 */
		public static createFuncMoveEquip(funcID:number,fromSlot:number,toSlot:number):FuncMoveEquipRequest
		{
			var re:FuncMoveEquipRequest=new FuncMoveEquipRequest();
			re.funcID=funcID;
			re.fromSlot=fromSlot;
			re.toSlot=toSlot;
			return re;
		}
		
	}
}
