namespace Shine
{
	/** 拆分物品(原物品至少保留1个)(generated by shine) */
	export class FuncSplitItemRequest extends FuncRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncSplitItem;
		
		/** 物品格子 */
		public index:number;
		
		/** 拆出数目 */
		public num:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncSplitItem;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FuncSplitItemRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.index);
			
			stream.writeInt(this.num);
			
		}
		
		/** 创建实例 */
		public static createFuncSplitItem(funcID:number,index:number,num:number):FuncSplitItemRequest
		{
			var re:FuncSplitItemRequest=new FuncSplitItemRequest();
			re.funcID=funcID;
			re.index=index;
			re.num=num;
			return re;
		}
		
	}
}
