namespace Shine
{
	/** 获取每页数据(主线程)(generated by shine) */
	export class FuncGetPageShowRequest extends FuncRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncGetPageShow;
		
		/** 页码 */
		public page:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncGetPageShow;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "FuncGetPageShowRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.page);
			
		}
		
		/** 创建实例 */
		public static createFuncGetPageShow(funcID:number,page:number):FuncGetPageShowRequest
		{
			var re:FuncGetPageShowRequest=new FuncGetPageShowRequest();
			re.funcID=funcID;
			re.page=page;
			return re;
		}
		
	}
}
