namespace Shine
{
	/** 申请加入玩家群消息(generated by shine) */
	export class FuncApplyRoleGroupRequest extends FuncRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncApplyRoleGroup;
		
		/** 群id */
		public groupID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncApplyRoleGroup;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.groupID);
			
		}
		
		/** 创建实例 */
		public static createFuncApplyRoleGroup(funcID:number,groupID:number):FuncApplyRoleGroupRequest
		{
			var re:FuncApplyRoleGroupRequest=new FuncApplyRoleGroupRequest();
			re.funcID=funcID;
			re.groupID=groupID;
			return re;
		}
		
	}
}
