namespace Shine
{
	/** 更改群名消息(generated by shine) */
	export class FuncChangeRoleGroupNameRequest extends FuncPlayerRoleGroupRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncChangeRoleGroupName;
		
		/** 名字 */
		public name:string;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncChangeRoleGroupName;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeUTF(this.name);
			
		}
		
		/** 创建实例 */
		public static createFuncChangeRoleGroupName(funcID:number,groupID:number,name:string):FuncChangeRoleGroupNameRequest
		{
			var re:FuncChangeRoleGroupNameRequest=new FuncChangeRoleGroupNameRequest();
			re.funcID=funcID;
			re.groupID=groupID;
			re.name=name;
			return re;
		}
		
	}
}
