namespace Shine
{
	/** 处理申请玩家群(generated by shine) */
	export class FuncHandleApplyRoleGroupRequest extends FuncPlayerRoleGroupRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncHandleApplyRoleGroup;
		
		public playerID:number;
		
		/** 处理结果 */
		public result:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncHandleApplyRoleGroup;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
			stream.writeInt(this.result);
			
		}
		
		/** 创建实例 */
		public static createFuncHandleApplyRoleGroup(funcID:number,groupID:number,playerID:number,result:number):FuncHandleApplyRoleGroupRequest
		{
			var re:FuncHandleApplyRoleGroupRequest=new FuncHandleApplyRoleGroupRequest();
			re.funcID=funcID;
			re.groupID=groupID;
			re.playerID=playerID;
			re.result=result;
			return re;
		}
		
	}
}
