namespace Shine
{
	/** 处理邀请玩家群消息(generated by shine) */
	export class FuncHandleInviteRoleGroupRequest extends FuncRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncHandleInviteRoleGroup;
		
		/** 邀请者 */
		public invitor:number;
		
		/** 群id */
		public groupID:number;
		
		/** 结果 */
		public result:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncHandleInviteRoleGroup;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.invitor);
			
			stream.writeLong(this.groupID);
			
			stream.writeInt(this.result);
			
		}
		
		/** 创建实例 */
		public static createFuncHandleInviteRoleGroup(funcID:number,invitor:number,groupID:number,result:number):FuncHandleInviteRoleGroupRequest
		{
			var re:FuncHandleInviteRoleGroupRequest=new FuncHandleInviteRoleGroupRequest();
			re.funcID=funcID;
			re.invitor=invitor;
			re.groupID=groupID;
			re.result=result;
			return re;
		}
		
	}
}
