namespace Shine
{
	/** 邀请加入玩家群消息(generated by shine) */
	export class FuncInviteRoleGroupRequest extends FuncRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncInviteRoleGroup;
		
		/** 玩家群id(-1为使用邀请直接创建) */
		public groupID:number;
		
		/** 玩家id */
		public playerID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncInviteRoleGroup;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.groupID);
			
			stream.writeLong(this.playerID);
			
		}
		
		/** 创建实例 */
		public static createFuncInviteRoleGroup(funcID:number,groupID:number,playerID:number):FuncInviteRoleGroupRequest
		{
			var re:FuncInviteRoleGroupRequest=new FuncInviteRoleGroupRequest();
			re.funcID=funcID;
			re.groupID=groupID;
			re.playerID=playerID;
			return re;
		}
		
	}
}
