namespace Shine
{
	/** 踢出成员消息(generated by shine) */
	export class FuncKickMemberRoleGroupRequest extends FuncPlayerRoleGroupRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncKickMemberRoleGroup;
		
		/** 成员id */
		public playerID:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncKickMemberRoleGroup;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
		}
		
		/** 创建实例 */
		public static createFuncKickMemberRoleGroup(funcID:number,groupID:number,playerID:number):FuncKickMemberRoleGroupRequest
		{
			var re:FuncKickMemberRoleGroupRequest=new FuncKickMemberRoleGroupRequest();
			re.funcID=funcID;
			re.groupID=groupID;
			re.playerID=playerID;
			return re;
		}
		
	}
}
