namespace Shine
{
	/** 设置职位(generated by shine) */
	export class FuncSetTitleRoleGroupRequest extends FuncPlayerRoleGroupRRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.FuncSetTitleRoleGroup;
		
		/** 角色id */
		public playerID:number;
		
		/** 职位 */
		public title:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.FuncSetTitleRoleGroup;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeLong(this.playerID);
			
			stream.writeInt(this.title);
			
		}
		
		/** 创建实例 */
		public static createFuncSetTitleRoleGroup(funcID:number,groupID:number,playerID:number,title:number):FuncSetTitleRoleGroupRequest
		{
			var re:FuncSetTitleRoleGroupRequest=new FuncSetTitleRoleGroupRequest();
			re.funcID=funcID;
			re.groupID=groupID;
			re.playerID=playerID;
			re.title=title;
			return re;
		}
		
	}
}
