namespace Shine
{
	/** 设置主引导步消息(generated by shine) */
	export class SetMainGuideStepRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.SetMainGuideStep;
		
		public value:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.SetMainGuideStep;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "SetMainGuideStepRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.value);
			
		}
		
		/** 创建实例 */
		public static createSetMainGuideStep(value:number):SetMainGuideStepRequest
		{
			var re:SetMainGuideStepRequest=new SetMainGuideStepRequest();
			re.value=value;
			return re;
		}
		
	}
}
