namespace Shine
{
	/** 兑换消息(generated by shine) */
	export class ExchangeRequest extends GameRequest
	{
		/** 数据类型ID */
		public static dataID:number=GameRequestType.Exchange;
		
		/** 兑换次数 */
		public num:number;
		
		/** 兑换ID */
		public id:number;
		
		constructor()
		{
			super();
			this._dataID=GameRequestType.Exchange;
		}
		
		protected copyData():void
		{
			super.copyData();
		}
		
		/** 获取数据类名 */
		public getDataClassName():string
		{
			return "ExchangeRequest";
		}
		
		/** 写入字节流(简版) */
		protected toWriteBytesSimple(stream:BytesWriteStream):void
		{
			super.toWriteBytesSimple(stream);
			
			stream.writeInt(this.id);
			
			stream.writeInt(this.num);
			
		}
		
		/** 创建实例 */
		public static createExchange(id:number,num:number):ExchangeRequest
		{
			var re:ExchangeRequest=new ExchangeRequest();
			re.id=id;
			re.num=num;
			return re;
		}
		
	}
}
